class RWC_Modal extends HTMLElement{constructor(){super(),this.init()}init(){this.attachShadow({mode:"open"}),this.shadowRoot.appendChild(this.getTemplate().content.cloneNode(!0))}getTemplate(){const n=document.createElement("template");return n.innerHTML='\n            <link rel="stylesheet" href="modal.min.css">\n\n            <div class="modal">\n                <div class="content">\n                    <div class="header">\n                        <h3>A Cool Heading</h3>\n                        <button type="button" class="close">\n                            <span>&times;</span>\n                        </button>\n                    </div>\n                    <div class="body">\n                        <div class="body-content">\n                            <p>The Body</p>\n                        </div>\n                    </div>\n                </div>\n            </div>\n        ',n}}window.customElements.define("rwc-modal",RWC_Modal);